Post.create!([
  {intab_id: 13, author_name: "관리자", author_id: 1, author_passwd: "bsr213417", title: "test1", file1: nil, file2: nil, ir1: "비공개 설정, 익명X", visible: true, anonymous: nil},
  {intab_id: 13, author_name: "관리자", author_id: 1, author_passwd: "bsr213417", title: "test2", file1: nil, file2: nil, ir1: "공개설정, 익명X", visible: false, anonymous: nil},
